import { Modal } from "@mui/material";
import React, { useState } from "react";
import { serviceDropdown } from "../data/navItems";
import { Link } from "react-scroll";

const Services = () => {
  // Modal handling
  const [open, setOpen] = useState(false);
  const [modalData, setModalData] = useState();
  // left & right button
  const [right, setRight] = useState(true);
  const [left, setLeft] = useState(true);

  //   Slide Right function
  const slideRight = () => {
    const slider = document.getElementById("slider");
    slider.scrollLeft = slider.scrollLeft + 380;
  };

  //   Slide left function
  const slideLeft = () => {
    const slider = document.getElementById("slider");
    slider.scrollLeft = slider.scrollLeft - 500;
  };

  //===================================================================================================================================
  // We will have a remove button logic as well when the space towards left or right is full remove that particular button
  // It will be applied to onScroll function on the main div
  //===================================================================================================================================
  const removeButton = (e) => {
    console.log(e.target.ScrollLeft, e.target.scrollWidth);
    if (e.target.scrollLeft == 0) {
      console.log("Hide Left Button");

      //setting the left to true as the condition passes
      setLeft(false);
      setRight(true); //right must be false when the left is true
    } else if (e.target.scrollLeft > 0) {
      setLeft(true);
      setRight(true);
    } else if (
      e.target.scrollLeft + e.target.clientWidth ===
      e.target.scrollWidth
    ) {
      console.log("Hide Right Button");

      //setting the right to true as the condition passes
      setRight(false);
      setLeft(true); // one value must be false so we can see only one button on the screen
    } else if (e.target.scrollLeft > 5980) {
      setRight(false);
      setLeft(true); // one value must be false so we can see only one button on the screen
    }
  };

  return (
    <div
      id='services'
      className='h-[700px]  w-full flex  justify-center items-center bg-blue-400'
    >
      {/* scroll cards  */}
      <div
        id='slider'
        className='w-full flex rounded-md overflow-hidden gap-5 overflow-x-scroll whitespace-nowrap scroll-smooth scrollbar-hide bg-white py-4 md:mx-28'
        // onScroll={removeButton}
      >
        {/* left arrow */}
        {left && (
          <button
            className='absolute w-[60px] h-[310px]  py-4  z-20 bg-white justify-center items-center left-13'
            onClick={slideLeft}
          >
            <img
              src='data:image/png;base64,'
              className=':w-14 opacity-100 cursor-pointer hover:opacity-80'
              alt='left button'
            />
          </button>
        )}

        {serviceDropdown.map((item) => (
          <div
            key={item.id}
            className='mx-20 bg-gray-200  h-[300px] min-w-[180px] sm:min-w-[220px] rounded-lg cursor-pointer hover:scale-105 ease-in-out duration-300 shadow-lg flex flex-col justify-center items-center'
            onClick={() => {
              setOpen(true);
              setModalData(item);
            }}
          >
            <img src={item.image} alt='services' className='w-[150px]' />
            <h1 className='text-sm  md:text-lg font-bold pt-10'>
              {item.title}
            </h1>
          </div>
        ))}

        <Modal open={open} onClose={(e) => setOpen(false)}>
          <div className=' h-screen bg-blue-200 m-8 md:m-16 lg:m-20 shadow-lg flex flex-col  items-center '>
            <h1 className='text-3xl font-semibold pt-16 text-center'>
              {modalData?.title}
            </h1>
            <p className='text-sm md:text-lg py-16 text-center px-4  sm:px-8 md:px-16 lg:px-36'>
              {modalData?.description}
            </p>
            <button className='flex justify-center cursor-pointer w-44 px-3 py-2 text-center m-3 border-none text-white bg-gradient-to-r from-indigo-300 to-blue-600 rounded-md hover:to-blue-800 active:transform active:scale-95'>
              <Link to='contact' onClick={() => setOpen(false)}>
                Order Now
              </Link>
            </button>
          </div>
        </Modal>
        {/* right button  */}
        {right && (
          <button
            className='absolute w-[60px] h-[310px]  py-4  z-20 bg-white justify-center items-center sm:right-28 right-1 '
            onClick={slideRight}
          >
            <img
              src='data:image/png;base64,'
              className='w-14 opacity-100 cursor-pointer hover:opacity-80'
              alt='right button'
            />
          </button>
        )}
      </div>
    </div>
  );
};

export default Services;
